// Main Package
package main

import (
    "html/template"
    "log"
    "net/http"
)

// Task Model
type Task struct {
    ID          int
    Title       string
    Description string
    Completed   bool
}

// Handler Types
type TaskHandler struct {
    tmpl  *template.Template
    tasks []Task
}

func NewTaskHandler() *TaskHandler {
    tmpl := template.Must(template.ParseGlob("templates/*.html"))
    return &TaskHandler{
        tmpl:  tmpl,
        tasks: make([]Task, 0),
    }
}

// List Handler
func (h *TaskHandler) List(w http.ResponseWriter, r *http.Request) {
    if r.Header.Get("HX-Request") == "true" {
        h.tmpl.ExecuteTemplate(w, "task-list.html", h.tasks)
        return
    }
    h.tmpl.ExecuteTemplate(w, "index.html", h.tasks)
}

// Create Handler
func (h *TaskHandler) Create(w http.ResponseWriter, r *http.Request) {
    if r.Method != http.MethodPost {
        http.Error(w, "Method not allowed", http.StatusMethodNotAllowed)
        return
    }

    r.ParseForm()
    task := Task{
        ID:          len(h.tasks) + 1,
        Title:       r.FormValue("title"),
        Description: r.FormValue("description"),
    }

    h.tasks = append(h.tasks, task)
    h.tmpl.ExecuteTemplate(w, "task-item.html", task)
}

// Toggle Handler
func (h *TaskHandler) Toggle(w http.ResponseWriter, r *http.Request) {
    if r.Method != http.MethodPut {
        http.Error(w, "Method not allowed", http.StatusMethodNotAllowed)
        return
    }

    id := r.URL.Query().Get("id")
    for i := range h.tasks {
        if h.tasks[i].ID == id {
            h.tasks[i].Completed = !h.tasks[i].Completed
            h.tmpl.ExecuteTemplate(w, "task-item.html", h.tasks[i])
            return
        }
    }
    http.Error(w, "Task not found", http.StatusNotFound)
}

// Main Function
func main() {
    handler := NewTaskHandler()

    http.HandleFunc("/", handler.List)
    http.HandleFunc("/tasks/create", handler.Create)
    http.HandleFunc("/tasks/toggle", handler.Toggle)

    log.Fatal(http.ListenAndServe(":8080", nil))
}

