// Utility Functions
const debounce = (fn, delay) => {
  let timeoutId;
  return (...args) => {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => fn(...args), delay);
  };
};

const observeIntersection = (element, callback, options = {}) => {
  const observer = new IntersectionObserver(entries => {
    entries.forEach(entry => {
      if (entry.isIntersecting) {
        callback(entry);
      }
    });
  }, options);

  observer.observe(element);
  return () => observer.disconnect();
};

// Form Validation
class FormValidator {
  constructor(form) {
    this.form = form;
    this.fields = {};
    this.init();
  }

  init() {
    this.form.setAttribute('novalidate', '');
    this.setupFields();
    this.bindEvents();
  }

  setupFields() {
    this.form.querySelectorAll('[data-validate]').forEach(field => {
      const rules = field.dataset.validate.split(',');
      this.fields[field.name] = { element: field, rules };
    });
  }

  validate() {
    let isValid = true;

    Object.entries(this.fields).forEach(([name, field]) => {
      const value = field.element.value;
      const errors = this.validateField(value, field.rules);

      if (errors.length) {
        isValid = false;
        this.showErrors(field.element, errors);
      } else {
        this.clearErrors(field.element);
      }
    });

    return isValid;
  }
}

