impl Buffer {
    #[wasm_bindgen(constructor)]
    pub fn new(size: usize) -> Buffer {
        Buffer {
            data: vec![0; size],
        }
    }

    pub fn get_ptr(&self) -> *const u8 {
        self.data.as_ptr()
    }

    pub fn len(&self) -> usize {
        self.data.len()
    }
}

// Error Handling
#[wasm_bindgen]
pub fn process_data(input: &[u8]) -> Result<Vec<u8>, JsValue> {
    input
        .try_into()
        .map_err(|e| JsValue::from_str(&e.to_string()))?
        .process()
        .map_err(|e| JsValue::from_str(&e.to_string()))
}
```

